import { CheckCircle, Phone, Home, Gift, Shield, Users, Star, Clock } from "lucide-react";
import { useState, useEffect } from "react";
import { useForm } from "react-hook-form";
import { zodResolver } from "@hookform/resolvers/zod";
import { z } from "zod";
import Header from "@/components/Header";
import Footer from "@/components/Footer";
import { Helmet } from "react-helmet-async";
import { useToast } from "@/hooks/use-toast";

const heroImage = "https://headless.cbisland.com/wp-content/uploads/2025/05/DJI_0561-1024x682.avif";

declare global {
  interface Window {
    jotformEmbedHandler: (selector: string, url: string) => void;
  }
}

const homeownerSchema = z.object({
  firstName: z.string().min(2, "First name is required"),
  lastName: z.string().min(2, "Last name is required"),
  email: z.string().email("Please enter a valid email address"),
  phone: z.string().min(10, "Please enter a valid phone number"),
  newHomeAddress: z.string().min(5, "Please enter your new home address"),
});

type HomeownerForm = z.infer<typeof homeownerSchema>;

export default function Homeowner() {
  const [isFormSubmitted, setIsFormSubmitted] = useState(false);
  const { toast } = useToast();

  const form = useForm<HomeownerForm>({
    resolver: zodResolver(homeownerSchema),
    defaultValues: {
      firstName: "",
      lastName: "",
      email: "",
      phone: "",
      newHomeAddress: "",
    },
  });

  useEffect(() => {
    const existingScript = document.querySelector('script[src*="for-form-embed-handler"]');

    if (existingScript) {
      if (window.jotformEmbedHandler) {
        window.jotformEmbedHandler("iframe[id='JotFormIFrame-232193796168266']", "https://pci.jotform.com/");
      }
      return;
    }

    const script = document.createElement('script');
    script.src = 'https://cdn.jotfor.ms/s/umd/latest/for-form-embed-handler.js';
    script.async = true;
    script.onload = () => {
      if (window.jotformEmbedHandler) {
        window.jotformEmbedHandler("iframe[id='JotFormIFrame-232193796168266']", "https://pci.jotform.com/");
      }
    };
    document.body.appendChild(script);

    return () => {
      if (script.parentNode) {
        script.parentNode.removeChild(script);
      }
    };
  }, []);

  const onSubmit = async (data: HomeownerForm) => {
    try {
      const response = await fetch('/api/homeowner-interest', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      const result = await response.json();

      if (response.ok) {
        setIsFormSubmitted(true);
        toast({
          title: "Request Submitted!",
          description: result.message || "We'll contact you soon about your $150 homeowner bonus.",
        });
        form.reset();
      } else {
        throw new Error(result.message || 'Failed to submit form');
      }
    } catch (error) {
      console.error('Form submission error:', error);
      toast({
        title: "Submission Failed",
        description: "Please call us at (902) 539-4242 or try again later.",
        variant: "destructive",
      });
    }
